import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Card, Button, Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [tasks, setTasks] = useState({ backlog: [], 'up-next': [], done: [] });

  useEffect(() => {
    fetch('https://api.airtable.com/v0/appVXFJJlBFs4nDeq/Design%20Projects?maxRecords=3&view=Main%20View', {
      headers: {
        'Authorization': 'Bearer patCRuvQNJDTr3dWj.a91f0bad66dc0006062fafebee44d99d20233350c1bc9cbfedcf70eab3283b44'
      }
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      console.log('Data from API:', data); // Log the data from the API
      const newTasks = { backlog: [], 'up-next': [], done: [] };
      data.records.forEach(record => {
        const task = {
          id: record.id,
          title: record.fields.Name,
          description: record.fields.Notes || ''
        };
        if (newTasks[record.fields.Status]) {
          newTasks[record.fields.Status].push(task);
        } else {
          console.warn(`Unexpected task status: ${record.fields.Status}`);
        }
      });
      setTasks(newTasks);
      console.log('Tasks after setting:', newTasks); // Log the tasks after they are set
    })
    .catch(error => console.log('Fetch error:', error)); // Log any fetch errors
  }, []);

  const handleDragEnd = (result) => {
    // TODO: handle drag and drop
  };

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <Container>
        <Row>
          {Object.entries(tasks).map(([columnId, tasks], index) => (
            <Col key={columnId} md={4}>
              <h2>{columnId}</h2>
              <Droppable droppableId={columnId}>
                {(provided) => (
                  <div ref={provided.innerRef} {...provided.droppableProps}>
                    {tasks.map((task, index) => (
                      <Draggable key={task.id} draggableId={task.id} index={index}>
                        {(provided) => (
                          <Card ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                            <Card.Body>
                              <Card.Title>{task.title}</Card.Title>
                              <Card.Text>{task.description}</Card.Text>
                            </Card.Body>
                          </Card>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </Col>
          ))}
        </Row>
      </Container>
    </DragDropContext>
  );
}

export default App;
