import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Card, Button, Container, Row, Col } from 'react-bootstrap';

const initialTasks = {
  'backlog': [
    { id: '1', title: 'Task 1', description: 'This is task 1' },
    { id: '2', title: 'Task 2', description: 'This is task 2' },
    { id: '3', title: 'Task 3', description: 'This is task 3' },
    { id: '4', title: 'Task 4', description: 'This is task 4' },
    { id: '5', title: 'Task 5', description: 'This is task 5' },
  ],
  'up-next': [
    { id: '6', title: 'Task 6', description: 'This is task 6' },
    { id: '7', title: 'Task 7', description: 'This is task 7' },
    { id: '8', title: 'Task 8', description: 'This is task 8' },
    { id: '9', title: 'Task 9', description: 'This is task 9' },
    { id: '10', title: 'Task 10', description: 'This is task 10' },
  ],
  'done': [
    { id: '11', title: 'Task 11', description: 'This is task 11' },
    { id: '12', title: 'Task 12', description: 'This is task 12' },
    { id: '13', title: 'Task 13', description: 'This is task 13' },
    { id: '14', title: 'Task 14', description: 'This is task 14' },
    { id: '15', title: 'Task 15', description: 'This is task 15' },
  ],
};

function App() {
  const [tasks, setTasks] = useState(initialTasks);

  const handleDragEnd = (result) => {
    // TODO: handle drag and drop
  };

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <Container>
        <Row>
          {Object.entries(tasks).map(([columnId, tasks], index) => (
            <Col key={columnId} md={4}>
              <h2>{columnId}</h2>
              <Droppable droppableId={columnId}>
                {(provided) => (
                  <div ref={provided.innerRef} {...provided.droppableProps}>
                    {tasks.map((task, index) => (
                      <Draggable key={task.id} draggableId={task.id} index={index}>
                        {(provided) => (
                          <Card ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                            <Card.Body>
                              <Card.Title>{task.title}</Card.Title>
                              <Card.Text>{task.description}</Card.Text>
                            </Card.Body>
                          </Card>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </Col>
          ))}
        </Row>
      </Container>
    </DragDropContext>
  );
}

export default App;
