import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Card, Button, Badge, Container, Row, Col, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [tasks, setTasks] = useState({ 'Priority Backlog': [], 'Today': [], 'Done Done': [] });
  const [showDoneDone, setShowDoneDone] = useState(true); // New state variable


  useEffect(() => {
    fetch('https://api.airtable.com/v0/appVXFJJlBFs4nDeq/Design%20Projects?filterByFormula=OR(%7BStatus%7D=\'Priority%20Backlog\',%7BStatus%7D=\'Today\',%7BStatus%7D=\'Done%20Done\')&sort%5B0%5D%5Bfield%5D=Priority&sort%5B0%5D%5Bdirection%5D=asc&sort%5B1%5D%5Bfield%5D=Size&sort%5B1%5D%5Bdirection%5D=asc&sort%5B2%5D%5Bfield%5D=Value&sort%5B2%5D%5Bdirection%5D=asc', {
      headers: {
        'Authorization': 'Bearer patCRuvQNJDTr3dWj.a91f0bad66dc0006062fafebee44d99d20233350c1bc9cbfedcf70eab3283b44'
      }
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      const newTasks = { 'Priority Backlog': [], 'Today': [], 'Done Done': [] };
      data.records.forEach(record => {
        const task = {
          id: record.id,
          title: record.fields.Name,
          description: record.fields.Notes || '',
          size: record.fields.Size // Add this line
        };
        if (newTasks[record.fields.Status]) {
          newTasks[record.fields.Status].push(task);
        } else {
          console.warn(`Unexpected task status: ${record.fields.Status}`);
        }
      });
      setTasks(newTasks);
      console.log('Tasks after setting:', newTasks); // Log the tasks after they are set
    })
    .catch(error => console.log('Fetch error:', error)); // Log any fetch errors
  }, []);

  const handleDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (!destination) {
      return;
    }

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const start = tasks[source.droppableId];
    const finish = tasks[destination.droppableId];
    const task = start.splice(source.index, 1)[0];
    finish.splice(destination.index, 0, task);
    setTasks({
      ...tasks,
      [source.droppableId]: start,
      [destination.droppableId]: finish,
    });

    const newStatus = destination.droppableId === 'Done' ? 'Done Done' : destination.droppableId;
    fetch(`https://api.airtable.com/v0/appVXFJJlBFs4nDeq/Design%20Projects/${draggableId}`, {
      method: 'PATCH',
      headers: {
        'Authorization': 'Bearer patCRuvQNJDTr3dWj.a91f0bad66dc0006062fafebee44d99d20233350c1bc9cbfedcf70eab3283b44',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        fields: {
          Status: newStatus
        }
      })
    })
    .then(response => response.json())
    .then(data => console.log(data))
    .catch(error => console.error(error));
  };

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <Container>
        <Row>
          {Object.entries(tasks).map(([columnId, tasks], index) => (
            <Col key={columnId} md={4}>
              <h2>
                {columnId} 
                {columnId === 'Done Done' && (
                  <Form.Check 
                    type="switch"
                    id="done-done-toggle"
                    label=""
                    checked={showDoneDone}
                    onChange={() => setShowDoneDone(!showDoneDone)}
                  />
                )}
              </h2>
              <Droppable droppableId={columnId}>
                {(provided) => (
                  <div ref={provided.innerRef} {...provided.droppableProps}>
                    {tasks.map((task, index) => (
                      (columnId !== 'Done Done' || showDoneDone) && (
                        <Draggable key={task.id} draggableId={task.id} index={index}>
                          {(provided) => (
                            <Card ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                              <Card.Body>
                                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                  <Card.Title>{task.title}</Card.Title>
                                  <Badge pill className="size-badge">{task.size}</Badge>
                                </div>
                                <Card.Text>{task.description}</Card.Text>
                              </Card.Body>
                            </Card>
                          )}
                        </Draggable>
                      )
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </Col>
          ))}
        </Row>
      </Container>
    </DragDropContext>
  );
}

export default App;
